// 檔案：Main.java
// 說明：示範使用 SwordsMan（所有輸出與註解以繁體中文顯示）
public class Main {
    public static void main(String[] args) {
        SwordsMan a = new SwordsMan("亞瑟", 100, 15);
        SwordsMan b = new SwordsMan("莉亞", 80, 20);

        System.out.println("戰鬥開始！");
        int round = 1;
        while (a.isAlive() && b.isAlive()) {
            System.out.println("第 " + round + " 回合：");

            a.attack(b);
            if (!b.isAlive()) {
                System.out.println(b.getName() + " 被擊敗！");
                break;
            }

            b.attack(a);
            if (!a.isAlive()) {
                System.out.println(a.getName() + " 被擊敗！");
                break;
            }

            System.out.println(a.getName() + " 生命：" + a.getHealth() + "，" + b.getName() + " 生命：" + b.getHealth());
            round++;
        }

        System.out.println("戰鬥結束。");
    }
}

// 檔案：SwordsMan.java
// 說明：劍士類別，所有註解及輸出文字以繁體中文顯示
class SwordsMan {
    // 名稱
    private String name;
    // 生命值
    private int health;
    // 攻擊力
    private int attackPower;

    // 建構子：初始化劍士
    public SwordsMan(String name, int health, int attackPower) {
        this.name = name;
        this.health = health;
        this.attackPower = attackPower;
    }

    // 取得名稱
    public String getName() {
        return name;
    }

    // 取得生命值
    public int getHealth() {
        return health;
    }

    // 取得攻擊力
    public int getAttackPower() {
        return attackPower;
    }

    // 攻擊對手，並輸出中文訊息
    public void attack(SwordsMan opponent) {
        opponent.health -= this.attackPower;
        System.out.println(this.name + " 攻擊 " + opponent.name + "，造成 " + this.attackPower + " 點傷害。");
    }

    // 判斷是否存活
    public boolean isAlive() {
        return health > 0;
    }
}

